import React, { Component } from 'react';
import FacebookLogin from 'react-facebook-login';

export default class Facebook extends Component {
    state = {
        isLoggedIn: false,
        userID: '',
        name: '',
        email: '',
        picture: ''
    }

    responseFacebook = response => {
        //console.log(response);

        this.setState({
            isLoggedIn: true,
            userID: response.userID,
            name: response.name,
            email: response.email,
            picture: response.picture.data.url,
            feed: response.feed
        });
    };

    componentClicked = () => console.log('clicado');

    render() {
        let fbContent;
        if(this.state.isLoggedIn){
            fbContent = (
                <div style={{
                    width:'400px',
                    margin:'auto',
                    background:'white',
                    padding:'20px'
                }}>
                    <img width='200px'src={this.state.picture} alt={this.state.name}/>
                    <h2 textColor='black'>Seja bem vindo, {this.state.name}</h2>
                    <h2>{this.state.email}</h2>
                    <h2>{this.state.userID}</h2>
                    <h2>{this.state.feed}</h2>
                    
                </div>
            );
        }else{
            fbContent = (<FacebookLogin
            appId = "509972076678422"
            autoLoad={true}
            fields="name,email,picture"
            onClick={this.componentClicked}
            callback={this.responseFacebook}
                />
            );
        }
        return (
            <div>
                {fbContent}
            </div>
        )
    }
}
